class likesToggle{constructor(t){this.element=t,this.togglelikes()}togglelikes(){$(this.element).click((function(t){t.preventDefault();let e=this;$.ajax({type:"POST",url:$(e).attr("href")}).done((function(t){let l=parseInt($(e).attr("id"));t.data.previouslyLiked?l--:l++,$(e).attr("id",l),$(e).html(l+' <i class="far fa-thumbs-up"></i>')})).fail((function(t){console.log("!!Error occured in likesToggle!!",t)}))}))}}